import { useState } from 'react'

function taskCreate() {

const [title, setTitle] = useState('');

const handleChacge=(e)=>{
    setTitle=(e.target.value)
};


  return (
    <div className='TaskDiv'>
        <h3>Task Ekleyiniz</h3>
        <form className='TaskForm'>
            <label className='taskLabel'>Başlık:</label>
            <input value={title} onChange={handleChacge} className='taskInput'/>
            <label className='taskLabel'>Task girin:</label>
            <textarea className='taskInput' rows={5}/>
            <button className='taskBtn'>Oluştur</button>
        </form>
    </div>
  )
}

export default taskCreate
